import React, { useState, useEffect } from 'react';

const Player = ({ currentSong }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [audio, setAudio] = useState(null);

  useEffect(() => {
    if (currentSong) {
      const newAudio = new Audio(currentSong.preview); // Usa il preview della canzone (se disponibile)
      setAudio(newAudio);
    }
  }, [currentSong]);

  const handlePlayPause = () => {
    if (isPlaying) {
      audio.pause();
    } else {
      audio.play();
    }
    setIsPlaying(!isPlaying);
  };

  const handleNext = () => {
    // Puoi implementare la logica per la canzone successiva
  };

  const handlePrev = () => {
    // Puoi implementare la logica per la canzone precedente
  };

  if (!currentSong) {
    return <div className="container-fluid fixed-bottom bg-container pt-1">No song selected</div>;
  }

  return (
    <div className="container-fluid fixed-bottom bg-container pt-1">
      <div className="row h-100">
        <div className="col-lg-10 offset-lg-2">
          <div className="row h-100 flex-column justify-content-center align-items-center">
            <div className="col-12 playerControls">
              <div className="d-flex justify-content-between">
                <a href="#" onClick={handlePrev}>
                  <img src="assets/prev.png" alt="prev" />
                </a>
                <a href="#" onClick={handlePlayPause}>
                  <img src={isPlaying ? "assets/pause.png" : "assets/play.png"} alt="play/pause" />
                </a>
                <a href="#" onClick={handleNext}>
                  <img src="assets/next.png" alt="next" />
                </a>
              </div>
              <div className="progress mt-3">
                <div role="progressbar" aria-valuenow="40" aria-valuemin="0" aria-valuemax="100"></div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="song-info text-center">
        <h4>{currentSong.title}</h4>
        <p>{currentSong.artist?.name}</p>
      </div>
    </div>
  );
};

export default Player;
