import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Sidebar from './components/Sidebar';
import MusicSection from './components/MusicSection';
import Player from './components/Player';
import { setSongs, setCurrentSong, toggleLike } from '../src/components/MusicSlice';

const App = () => {
  const dispatch = useDispatch();
  const { songs, likedSongs, currentSong } = useSelector((state) => state.music);

  useEffect(() => {
    const fetchData = async (artistName) => {
      try {
        const response = await fetch(
          `https://striveschool-api.herokuapp.com/api/deezer/search?q=${artistName}`
        );
        if (response.ok) {
          const { data } = await response.json();
          dispatch(setSongs(data.slice(0, 4))); // Imposta le prime 4 canzoni
        }
      } catch (err) {
        console.error('Error:', err);
      }
    };

    fetchData('queen');
    fetchData('idles');
    fetchData('eminem');
    fetchData("radiohead")
  }, [dispatch]);

  return (
    <div className="container-fluid">
      <div className="row">
        <Sidebar />
        <main className="col-12 col-md-9 offset-md-3 mainPage">
          <div className="row">
            <MusicSection title="Rock Classics" data={songs} toggleLike={toggleLike} />
            <MusicSection title="Pop Culture" data={songs} toggleLike={toggleLike} />
            <MusicSection title="#HipHop" data={songs} toggleLike={toggleLike} />
          </div>
        </main>
      </div>
      <Player currentSong={currentSong} />
    </div>
  );
};

export default App;
